import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import dateFormat from 'dateformat';
import * as pdfMake from 'pdfmake/build/pdfmake.js';
import * as pdfFonts from 'pdfmake/build/vfs_fonts.js';
import { PageSize, PageOrientation, Alignment, Margins } from 'pdfmake/interfaces';
import { UserService } from 'src/app/services/user.service';
(pdfMake as any).vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-alltraining',
  templateUrl: './alltraining.component.html',
  styleUrls: ['./alltraining.component.css']
})
export class AlltrainingComponent implements OnInit {

  training: any = []
  constructor(private userService: UserService) { }

  ngOnInit(): void {
    console.log(localStorage.getItem('company'))
    this.userService.getTraining().subscribe(
      (res: any) => {
        if (res.length > 0) {
          for (let i = 0; i < res.length; i++) {
              this.training.push(res[i])
          }
        }
      },
      (err) => {
        console.log(err)
      }
    )



  }


  allSearch(allSearchForm: NgForm) {
    this.training = []
    this.userService.getTraining().subscribe(
      (res: any) => {
        let employeeName = allSearchForm.value.name.toLowerCase()
        let employeeTraining = allSearchForm.value.training.toLowerCase()
        let company = allSearchForm.value.company.toLowerCase()
        if (res.length > 0) {
          for (let i = 0; i < res.length; i++) {
            let resName = res[i].name.toLowerCase()
            let resTraining = res[i].training.toLowerCase()
            let resCompany = res[i].company.toLowerCase()
            if (resName.includes(employeeName) && resTraining.includes(employeeTraining) && resCompany.includes(company)) {
              this.training.push(res[i])
            }
          }
        }
      },
      (err) => {
        console.log(err)
      }
    )

  }

  public generatePDF(name: any, training: any, date: any, expDate: any, instructor: any) {
    let docDefinition = {
      pageSize: 'A5' as PageSize,
      pageOrientation: 'landscape' as PageOrientation,
      content: [
        {
          text: 'Certificate of Completion',
          style: 'header'
        },
        { text: 'This is to certify that', style: 'para' },
        {
          text: name,
          style: 'subheader'
        },
        { text: `has completed the following training on ${dateFormat(date, 'shortDate')}`, style: 'para' },
        {
          text: training,
          style: 'subheader'
        },
        { text: `Instructor: ${instructor} \t\t\t Expiration Date: ${dateFormat(expDate, 'shortDate')}`, style: 'para' },
        {
          image: 'data:image/png;base64,',
          width: 150,
          style: 'image'
        }

      ],
      styles: {
        header: {
          fontSize: 18,
          bold: true,
          alignment: 'center' as Alignment,
          margin: [0, 10, 0, 20] as Margins
        },
        subheader: {
          fontSize: 15,
          bold: true,
          alignment: 'center' as Alignment,
          margin: [0, 0, 0, 15] as Margins
        },
        para: {
          alignment: 'center' as Alignment,
          margin: [0, 0, 0, 15] as Margins
        },
        image: {
          alignment: 'center' as Alignment,
          margin: [0, 10, 0, 0] as Margins
        }
      }
    };

    pdfMake.createPdf(docDefinition).open();
  }

}
